var tree = new YAHOO.widget.TreeView("tree-div");
tree.render(); 
var count = tree.getNodeCount();
var tn;
for(var i=1;i<=count;i++){
	tn = tree.getNodeByIndex(i);
	tn.expand(); 
}

/* 保持选定状态 */
var items = YAHOO.util.Dom.getElementsByClassName("ygtvcontent");
var lastSelect = null;
var selectedClassName = "snow-focus";
YAHOO.util.Event.on(items,"click",function(){
	YAHOO.util.Dom.removeClass(lastSelect,selectedClassName);
	if(lastSelect){
		YAHOO.util.Dom.removeClass(YAHOO.util.Dom.getPreviousSibling(lastSelect),selectedClassName);
	}
	lastSelect = this;
	var thisPre = YAHOO.util.Dom.getPreviousSibling(this);
	YAHOO.util.Dom.addClass([thisPre,this],selectedClassName);
})

